extension ViewController: NSCollectionViewDataSource {
    
    static let labelItem = "LabelItem"
    
    func numberOfSections(in collectionView: NSCollectionView) -> Int {
        return sections.count
    }
    
    func collectionView(_ collectionView: NSCollectionView, numberOfItemsInSection section: Int) -> Int {
        return sections.count
    }
    
    func collectionView(_ collectionView: NSCollectionView, itemForRepresentedObjectAt indexPath: IndexPath) -> NSCollectionViewItem {
        let cell = collectionView.makeItem(
            withIdentifier: NSUserInterfaceItemIdentifier(rawValue: ViewController.labelItem),
            for: indexPath
        )
        
        let emoji = emojis[indexPath.item].emoji
        cell.textField?.stringValue = emoji
        
        return cell
    }
    
    func collectionView(_ collectionView: NSCollectionView, viewForSupplementaryElementOfKind kind: String, at indexPath: IndexPath) -> NSView {
        let view = collectionView.makeSupplementaryView(ofKind: NSCollectionView.elementKindSectionHeader, withIdentifier: NSUserInterfaceItemIdentifier(rawValue: "HeaderView"), for: indexPath) as! HeaderView
        print( "indexPath", indexPath )
        view.sectionName.stringValue = sections[indexPath.item]
        let numberOfItemsInSection = sections[indexPath.item]
        return view
    }
    
}

